<script>
import { get } from "utils/http";
import Footer from "components/mainPage/Footer";
import BScroll from "better-scroll";
import { async } from "q";

export default {
  data() {
    return {
      relatedInfos: "",
      userInfos: "",
      userCount: "",
      BrieDetailInfos: "",
      listenAlbum: "",
      totalCounts: "",
      DetailInfos: "",
      type: "",
      currentResults: "",
      titles: "",
      imgUrl: "",
      audioUrl: "",
      anchorId: "",
      commentsData: "",
      isZhan: false,
      Count: "",
      Br: "",
      bre: "",
      pause:false,
      play:true,
      pageSize:0,
      pages:1,
      none:false,
      fid:'',
      fidd:''
    };
  },
  getData(result){
    this.DetailInfos=[
      ...this.DetailInfos,
      ...result.data.trackDetailInfos
    ]
  },
  methods: {
    zhanClick() {
      this.isZhan = true;
    },
    handelClick(id){
      console.log(id);
      this.$router.push(`/play/${this.fid}/${id}`)
      this.$router.go(0);
    },
    playPulse(){
      var audio = document.getElementById('audio')
      if(audio.paused){
        audio.play()
        this.play = false
        this.pause = true
      }else{
        audio.pause()
        this.play = true
        this.pause = false
      }
    },
     async getMore(){
      if(this.pages< Math.ceil(this.totalCounts/this.pageSize)){
        this.pages++
        let pageResult = await get({
          url:
          `/ajax/m-revision/common/album/queryAlbumTrackRecordsByPage?albumId=${this.fid}&page=${this.pages}&pageSize=10&asc=true`
        });
        // this.totalCounts = pageResult.data.totalCount;
        // this.DetailInfos = pageResult.data.trackDetailInfos;
        this.$options.getData.call(this,pageResult)
        // for(var i=0;i<this.DetailInfos.length;i++){
        //   // console.log(this.DetailInfos[i].id,this.fidd);
        //   if(this.DetailInfos[i].id == this.fidd){
        //     console.log(i);
        //   }
        // }
      }else{
        this.none = true
        console.log('到底了');
        
      }
      
    }
  },
  async mounted() {
    var bScroll = new BScroll("#award", {
      pullUpLoad: true,
      click: true,
      probeType: 2
    });
    //从地址中获取id和idd
    let req = /[^\/](\d+)/g;
    let resultId = this.$route.path.match(req);
    let id = resultId[0];
    let idd = resultId[1];
    this.fid = id
    this.fidd = idd
    let anchor = await get({
      url: `/ajax/m-revision/page/track/queryTrackPage/${idd}`
    });
    this.anchorId = anchor.data.anchorInfo.id;

    let anchorResult = await get({
      url: `/ajax/m-revision/common/anchor/queryAnchorAlbumsByPage?anchorId=${this.anchorId}&page=1&pageSize=3&asc=false`
    });
    this.BrieDetailInfos = anchorResult.data.albumBriefDetailInfos[0].albumInfo;
    this.Br = anchorResult.data.albumBriefDetailInfos[0].statCountInfo;
    this.bre = anchorResult.data.albumBriefDetailInfos;
    // console.log(this.BrieDetailInfos);

    let pageResult = await get({
      url: `/ajax/m-revision/common/album/queryAlbumTrackRecordsByPage?albumId=${id}&page=1&pageSize=10&asc=true`
    });
    this.totalCounts = pageResult.data.totalCount;
    let sizes = pageResult.data.pageSize
    this.pageSize = (sizes > this.totalCounts) ? this.totalCounts : sizes

    this.DetailInfos = pageResult.data.trackDetailInfos;
    this.type = this.DetailInfos[0].pageUriInfo.categoryCode;

    
    // console.log(this.DetailInfos,idd);
    


    let result = await get({
      url: `/ajax/mobile/v1/track/share/content?trackId=${idd}&tpName=weixin&device=h5`
    });
    // console.log(result);
    this.imgUrl = result.weixinPic;
    this.audioUrl = result.audioUrl;

    let currentResult = await get({
      url: `/ajax/revision/seo/getTdk?typeName=TRACK&uri=%2F${this.type}%2F${id}%2F${idd}&tdkType=wap`
    });
    this.currentResults = currentResult.data.tdkMeta.title;
    // console.log(this.currentResults);

    let req2 = /\w+/;
    this.titles = this.currentResults.split("_")[0];
    // console.log(this.titles);

    let resulta = await get({
      url: `/ajax/m-revision/page/album/v2/queryAlbumPage/${id}?albumCounts=track`
    });
    this.Count = resulta.data.albumDetailInfo.statCountInfo;
    document.getElementById("richIntros").innerHTML =
      resulta.data.albumRichInfo.richIntro;

    // console.log(this.imgUrl, this.audioUrl);

    let relatedResult = await get({
      url: `/ajax/m-revision/common/album/queryAlbumRelatedRecommendAlbums?albumId=${id}&page=1&pageSize=6`
    });
    this.relatedInfos = relatedResult.data.albumBriefDetailInfos;

    // console.log(this.relatedInfos);

    let listen = await get({
      url: "/ajax/m-revision/common/album/queryClassicListenAlbums"
    });
    this.listenAlbum = listen.data;

    // console.log( this.BrieDetailInfos );

    let userResult = await get({
      url: `/ajax/m-revision/common/user/queryUserInfo/${this.anchorId}?userCountKeys=follower`
    });
    this.userInfos = userResult.data.userInfo;
    this.userCount = userResult.data.statCountInfo;

    let comments = await get({
      url: `/ajax/m-revision/common/track/queryTrackCommentsFirstPage?trackId=${idd}&pageSize=10&page=1`
    });
    this.commentsData = comments.data;
    // let cc = document.getElementsByClassName("comments")
    // for(var i=0;i<this.commentsData.comments.length;i++)
    //   cc[i].innerHTML = this.commentsData.comments[i].content;
  },
  components: {
    Footer
  }
};
</script>
